		}


		for (var i = 0; i < clickToCopies.length; i++) {

			clickToCopies[i].addEventListener('click', function(){
				var clickToCopy = jQuery(this);
				var code = clickToCopy.find('span').text();
				code = code.replace(/\s+/g, '');
				if (code != "COPIED!") {
					navigator.clipboard.writeText(code);
					clickToCopy.find(ltkCouponCode).html('COPIED!');  

					setTimeout(function () {
						jQuery('.ltkpopup-icon-ctc '+ltkCouponCode).text(code);
