function getUrlParameter(name) {
	name = name.replace(/[\[]/, '\\[').replace(/[\]]/, '\\]');
	var regex = new RegExp('[\\?&]' + name + '=([^&#]*)');
	var results = regex.exec(location.search);
	return results === null ? '' : decodeURIComponent(results[1].replace(/\+/g, ' '));
}

function ltkGoToPage(pageNum) {
	var visiblePage = document.querySelector('[data-ltkpage].page-visible');
	var pages = document.querySelectorAll('[data-ltkpage]');
	if( pageNum == undefined ) {
		pageNum = Number(visiblePage.getAttribute('data-ltkpage'))+1;

	}
	if( pageNum > pages.length ) {
		pageNum = 1;
	}
	if( visiblePage ) {
		// visiblePage.classList.remove('page-visible');
		var allPages = document.querySelectorAll('[data-ltkpage]');
		for (var i = 0; i < allPages.length; i++) {
			var element = allPages[i];
			element.classList.remove('page-visible');

		}
		document.querySelector('[data-ltkpage="'+pageNum+'"]').classList.add('page-visible');
	}

	var urlParams =  new URL(window.location.href);
	urlParams.searchParams.set('data-ltkpage', pageNum);
	window.history.pushState({}, '', urlParams.toString());
}

if( window.location.href.indexOf("data-ltkpage") > -1 ) {
	ltkGoToPage(getUrlParameter('data-ltkpage'));
}

