	<script>
	//handle validation
	var elements = document.querySelectorAll("input,select");
	var invalidClassName = " ltkinputnotvalid";
	for (var i = 0; i < elements.length; i++) {
		elements[i].addEventListener("invalid", function(e) {
			if (!e.target.validity.valid) {
				AddClassToElement(e.target, invalidClassName);
				if (ElementHasClass(e.target.parentNode, "dropdown"))
					AddClassToElement(e.target.parentNode, invalidClassName);
			}
		});
		elements[i].addEventListener("change", function(e) {
			RemoveClassFromElement(e.target, invalidClassName);
			if (ElementHasClass(e.target.parentNode, "dropdown"))
				RemoveClassFromElement(e.target.parentNode, invalidClassName);
		});
	}
	var AddClassToElement = function(elem, cls) {
		elem.className += cls;
	}
	var RemoveClassFromElement = function(elem, cls) {
		elem.className = elem.className.replace(cls, "");
	}
	var ElementHasClass = function(elem, cls) {
		return elem.className.indexOf(cls) > -1;
	}

	var forms = document.getElementById('ltkpopup-submit');
	for (var i = 0; i < forms.length; i++) {
		forms[i].noValidate = true;

		forms[i].addEventListener('click', function(event) {
			//Prevent submission if checkValidity on the form returns false.
			if (!event.target.checkValidity()) {
				event.preventDefault();
			}
		}, false);
	}

	function checkInputValidity(inputEl) {
	setTimeout(function () {
		jQuery(inputEl).removeClass("ltkinputnotvalid");
		var ltkErrorMessage = jQuery("[data-msg-for='" + inputEl.id + "']");
		jQuery(ltkErrorMessage).text("");
		inputEl.checkValidity();

		var label = document.querySelector('label[for="' + inputEl.id + '"]');
		if (jQuery(inputEl).is(':invalid')) {
			jQuery(inputEl).addClass("ltkinputnotvalid");
			inputEl.setAttribute('aria-describedby', inputEl.id + '-error-message');
			jQuery(inputEl).closest(".dropdown").addClass("ltkinputnotvalid");
			var ltkErrorMessageText = jQuery(ltkErrorMessage).attr("data-error-message");
			jQuery(ltkErrorMessage).text(ltkErrorMessageText);
			if (label != null) {
				label.classList.add("ltkinputnotvalid");
			}
		} else {
			jQuery(inputEl).closest(".dropdown").removeClass("ltkinputnotvalid");
			inputEl.removeAttribute('aria-describedby');
			if (label != null) {
				label.classList.remove("ltkinputnotvalid");
			}
		}

		jQuery(document).trigger('ltkResize');

	}, 0);

}

	function limitBdays() {
		var dayEl = document.querySelector("select#bday");
		var monthEl = document.querySelector("select#bmonth");
		var days = 31;
		var month = monthEl.value;
		console.log(month)
		if (month == 2) {
			days = 29;
		} else if (month == 2 || month == 4 || month == 6 || month == 9 || month == 11) {
			days = 30;
		}
		for (var i = 1; i < 32; i++) {
			if (i > days) {
				jQuery("#" + dayEl.id + " option[value='" + i + "']").prop('disabled', true);
				jQuery("#" + dayEl.id + " option[value='" + i + "']").hide();
				jQuery("[data-hidden-select-id='" + dayEl.id + "'] li[data-value='" + i + "']").hide();

				if (dayEl.value == i) {
					dayEl.value = "";
					var event = document.createEvent("Event");
					event.initEvent("change", false, true);
					dayEl.dispatchEvent(event);
				}
			} else if (document.querySelector("#" + dayEl.id + " option").hasAttribute("disabled")) {
				jQuery("#" + dayEl.id + " option[value='" + i + "']").removeAttr('disabled');
				jQuery("#" + dayEl.id + " option[value='" + i + "']").show();
				jQuery("[data-hidden-select-id='" + dayEl.id + "'] li[data-value='" + i + "']").show();
			}
		}

	}
	if (document.querySelector("select#bmonth") != null && document.querySelector("select#bday") != null) {
		document.getElementById("bmonth").addEventListener("change", function () {
			limitBdays();
		});
		limitBdays(document.getElementById("bmonth"));
	}
	function ltkDateChange(){
		var ltkMonth = document.getElementById('bmonth').value;
		var ltkDay = document.getElementById('bday').value;
		var ltkHidden = document.getElementById('ltkpopup-dob');

		if(ltkMonth && ltkDay) {
			ltkHidden.value = ltkMonth + '/' + ltkDay + '/' + '1900';
		}
	}
